export declare const matcapFunction = " vec2 matcapFunction(vec3 eye, vec3 normal) {\n    vec3 reflected = reflect(eye, normal);\n    float m = 2.8284271247461903 * sqrt( reflected.z+1.0 );\n    return reflected.xy / m + 0.5;\n}";
export declare const textureLevel = "float textureLevel(in sampler2D sampler, in vec2 v_st){\n    vec2 size = vec2(textureSize(sampler, 0));\n\n    float levelCount = max(log2(size.x), log2(size.y));\n\n    vec2 dx = dFdx(v_st * size);\n    vec2 dy = dFdy(v_st * size);\n    float d = max(dot(dx, dx), dot(dy, dy));\n\n    d = clamp(d, 1.0, pow(2.0, (levelCount - 1.0) * 2.0));\n\n    return 0.5 * log2(d);\n}";
